        int d[] = {-1, 0, 1, 0, -1};
        for (int i = 0; i < 4; ++i) {
            int u = x + d[i];
            int v = y + d[i + 1];
            if (isValid(u, v) && grid[u][v] == 1 && visited[u][v] == false) {
                cnt += dfs(u, v, par, grid);
            }
        }
        return cnt;
