class Solution {
    public int largestIsland(int[][] grid) {

        int gridSize = grid.length * grid[0].length;

        // find connecting points between two islands
        // basically 0s sorrounded by at least two 1s
        Set<int[]> connections = new HashSet<>();
        for (int i = 0; i < grid.length; i++) {
            for (int j = 0; j < grid[0].length; j++) {
                int count = 0;
                if (grid[i][j] == 0) {
                    count += i > 0 ? grid[i - 1][j] : 0;
                    count += i < grid.length - 1 ? grid[i + 1][j] : 0;
                    count += j > 0 ? grid[i][j - 1] : 0;
                    count += j < grid[0].length - 1 ? grid[i][j + 1] : 0;
                    if (count > 1) {
                        connections.add(new int[] { i, j });
                    }
                }
            }
        }

        if (connections.isEmpty()) {
            // no connecting points
            int count = 0;
            for (int i = 0; i < grid.length; i++) {
                for (int j = 0; j < grid[0].length; j++) {                
                    if (grid[i][j] == 1) {
                        count++;
                    }
                }
            }
            // edge case all 1s
            return count == gridSize ? count : count + 1;
        }

        // islands are named starting with 2 and incrementing by 1
        // maintaing count per island
        Map<String, Integer> islandSizeMap = new HashMap<>();        
        int island = 2;

        for (int i = 0; i < grid.length; i++) {
            for (int j = 0; j < grid[0].length; j++) {

                if (grid[i][j] != 1) {
                    // previously calculated island or 0. ignore it.
                    continue;
                }

                Queue<int[]> queue = new LinkedList<>();
                queue.add(new int[] { i, j });

                int size = 0;
                String name = String.valueOf(island);

                while (!queue.isEmpty()) {
                    int[] pos = queue.poll();
                    int row = pos[0];
                    int col = pos[1];

                    if (grid[row][col] != 1) {
                        // we could add duplicate 1s whose value must have been
                        // already updated with island number. ignore them.
                        continue;
                    }

                    grid[row][col] = island;                    
                    size++;

                    if (row > 0 && grid[row - 1][col] == 1) {
                        queue.add(new int[] { row - 1, col });
                    }
                    if (row < grid.length - 1 && grid[row + 1][col] == 1) {
                        queue.add(new int[] { row + 1, col });
                    }
                    if (col > 0 && grid[row][col - 1] == 1) {
                        queue.add(new int[] { row, col - 1 });
                    }
                    if (col < grid[0].length - 1 && grid[row][col + 1] == 1) {
                        queue.add(new int[] { row, col + 1 });
                    }
                }

                // add island size per island
                islandSizeMap.put(name, size);   
                // proceed to name next island
                island++;
            }
        }

        int maxSize = 0;        

        for (int[] connection : connections) {
            int row = connection[0];
            int col = connection[1];

            // finding connecting islands for every connection point
            Set<Integer> neighborIslands = new HashSet<>(4);

            if (row > 0 && grid[row - 1][col] > 0) {
                neighborIslands.add(grid[row - 1][col]);
            }
            if (row < grid.length - 1 && grid[row + 1][col] > 0) {
                neighborIslands.add(grid[row + 1][col]);
            }
            if (col > 0 && grid[row][col - 1] > 0) {
                neighborIslands.add(grid[row][col - 1]);
            }
            if (col < grid[0].length - 1 && grid[row][col + 1] > 0) {
                neighborIslands.add(grid[row][col + 1]);
            }

            int total = 0;
            for (int neighborIsland : neighborIslands) {
                total += islandSizeMap.get(String.valueOf(neighborIsland));
            }
            // comparing the total size against max found so far
            maxSize = Math.max(maxSize, total);
        }

        // +1 for including the connection point
        return maxSize + 1;
    }    
}
