class Solution {
    private Map<Integer, Integer> areaMap;
    private int N;

    // up, down, left, right
    private int[][] moves = {
        { -1, 0 }, { 1, 0 }, { 0, -1 }, { 0, 1 }
    };

    private int getIslandArea(int[][] grid, int index, int r, int c) {
        int islandArea = 1;

        grid[r][c] = index;

        for (int[] move : moves) {
            int nr = r + move[0];
            int nc = c + move[1];

            if (nr < 0 || nc < 0 || nr >= N || nc >= N || grid[nr][nc] != 1) {
                continue;
            }

            islandArea += getIslandArea(grid, index, nr, nc);   
        }

        return islandArea;
    }

    private int getNeighborIslandArea(int[][] grid, int r, int c) {
        int neighborAreas = 1;

        Set<Integer> visitedIslands = new HashSet<>();

        for (int[] move : moves) {
            int nr = r + move[0];
            int nc = c + move[1];

            if (nr < 0 || nc < 0 || nr >= N || nc >= N) {
                continue;
            }

            int index = grid[nr][nc];

            if (index > 0 && !visitedIslands.contains(index)) {
                neighborAreas += areaMap.get(index);
                visitedIslands.add(index);
            }
        }

        return neighborAreas;
    }

    public int largestIsland(int[][] grid) {
        this.N = grid.length;
        this.areaMap = new HashMap<>();

        int islandIndex = 2;
        int maxIsland = 0;

        // Find areas and map island index to grid
        for (int r = 0; r < N; r++) {
            for (int c = 0; c < N; c++) {
                if (grid[r][c] == 1) {
                    int area = getIslandArea(grid, islandIndex, r, c);
                    maxIsland = Math.max(maxIsland, area);
                    areaMap.put(islandIndex++, area);
                }
            }
        }

        // Connect two or more neighboring islands
        for (int r = 0; r < N; r++) {
            for (int c = 0; c < N; c++) {
                if (grid[r][c] == 0) {
                    int area = getNeighborIslandArea(grid, r, c);
                    maxIsland = Math.max(maxIsland, area);
                }
            }
        }

        return maxIsland;
    }
}
