class Solution {
public:

    // for calculating the area and 'unique-id' of the island
     void dfs(vector<vector<int>> &grid, unordered_map<int,int>&mp, int i, int j, int island_id)
     {
        if(i<0 || j<0 || i>=grid.size() || j>=grid[0].size())
            return;
        if(grid[i][j]!=1)
            return;

        mp[island_id]++; // (id, area)
        grid[i][j] = island_id; // It is also marked as visited automatically while setting the 'id'

        dfs(grid,mp,i+1,j,island_id);
        dfs(grid,mp,i-1,j,island_id);
        dfs(grid,mp,i,j+1,island_id);
        dfs(grid,mp,i,j-1,island_id);
    }

    int largestIsland(vector<vector<int>>& grid) {

        int m=grid.size(), n=grid[0].size(), island_id=2;
        unordered_map<int,int> mp;
        int dx[4]={1,-1,0,0};
        int dy[4]={0,0,1,-1};

        int area=0;        
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(grid[i][j]==1){
                    dfs(grid,mp,i,j,island_id);
					// We have to find the largest island in case there are no 0's in the grid
                    area=max(area,mp[island_id]);// It is useful when all islands are far away for each other by two or more cells
                    island_id++;
                }
           }
        }


        // BFS starts...
        // we will push the 0's in the queue. From each 0, we will start a bfs of size 1
        queue<pair<int,int>> q;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(grid[i][j]==0)
                    q.push({i,j});
            }
        }

        while(!q.empty())
        {
            auto p = q.front();
            q.pop();
            int x = p.first;
            int y = p.second;
            // Unordered Set to make sure the islands are unique
            unordered_set<int> s;
            int tmp = 1; // tmp == 1 because current cell also counts in area (when it is changing from '0' to '1')
            for(int i=0;i<4;i++)
            {
                int r = x+dx[i];
                int c = y+dy[i];
                if(r>=0 && r<=m-1 && c>=0 && c<=n-1 && grid[r][c] != 0)
                {
                    // BFS of Size - 1 
					// we do not need to push here because we pushed  already all coordinates where cell has  'zero' 
                    int i_id = grid[r][c]; // get island id  
                    if(s.find(i_id)==s.end()){ // using set for Optimization : because it may be possible that cell has '0' surrounded by same island by more than one sides. out of '4'
                        s.insert(i_id);
                        tmp+=mp[i_id]; //summation of area of all islands which are near to '0' cell. 
                    }    
                }
            }

            area = max(area, tmp);

        }
        return area;
    }
};
