class Solution:
    def largestIsland(self, grid: List[List[int]]) -> int:        
        maxIslandSize = 0
        IslandSizeList = [0, 0] # we start the index at 2, because 0, 1 in already used in question
        IslandTag = 2 # we start the index at 2

