                    maxIslandSize = max(maxIslandSize, islandSize)
                    IslandSizeList.append(islandSize) # store each island size into the IslandSizeList
                    IslandTag += 1

        # In our second step, 
        # we will go through the grid again and find the 0s inside the grid.
        # And we check that zero's top, bottom, right, left to see all the island, 
        # and we add all the island size.
        # Last, we always get the greater size value into our maxIslandSize which is the answer.
        for column in range(len(grid)):
            for row in range(len(grid[0])):
                tmpIslandSize = 0
                if grid[column][row] == 0:
                    tmp = []
                    if row > 0 and grid[column][row-1] != 0 and grid[column][row-1] not in tmp:
                        tmp.append(grid[column][row-1])
                        tmpIslandSize += IslandSizeList[grid[column][row-1]]
                    if row < len(grid[0])-1 and grid[column][row+1] != 0 and grid[column][row+1] not in tmp:
                        tmp.append(grid[column][row+1])
                        tmpIslandSize += IslandSizeList[grid[column][row+1]]
                    if column > 0 and grid[column-1][row] != 0 and grid[column-1][row] not in tmp:
                        tmp.append(grid[column-1][row])
                        tmpIslandSize += IslandSizeList[grid[column-1][row]]
                    if column < len(grid)-1 and grid[column+1][row] != 0 and grid[column+1][row] not in tmp:
                        tmp.append(grid[column+1][row])
                        tmpIslandSize += IslandSizeList[grid[column+1][row]]

                    maxIslandSize = max(maxIslandSize, tmpIslandSize+1)

        return maxIslandSize


    def helper(self, grid, size, row, column, IslandTag):
        grid[column][row] = IslandTag # give the IslandTag into the grid
        # If four sides are invalid, then we return.
        if not 0 <= row < len(grid[0]) or not 0 <= column < len(grid): 
            return size

        # for each side, we check whether is boundary and whether is 1(island).
        if (row + 1) < len(grid[0]) and grid[column][row+1] == 1:
            right = self.helper(grid, size+1, row+1, column, IslandTag)
        else:
            right = size

        if (row - 1) >= 0 and grid[column][row-1] == 1:
            left = self.helper(grid, size+1, row-1, column, IslandTag)
        else:
            left = size            

        if (column + 1) < len(grid) and grid[column+1][row] == 1:
            bottom = self.helper(grid, size+1, row, column+1, IslandTag)
        else:
            bottom = size

        if (column - 1) >= 0 and grid[column-1][row] == 1:
            top = self.helper(grid, size+1, row, column-1, IslandTag)
        else:
            top = size


        return right + left + bottom + top - size*3
