class Solution {

    int N;
    int size = 0;
    int[][] dir = new int[][]{{0,1},{0,-1},{-1,0},{1,0}};
    public int largestIsland(int[][] grid) {
        Map<Integer, Integer> map = new HashMap<>();
        int res = 0;
        N = grid.length;
        int colorId = 2; // because the board val it's either 1 or 0
        for (int i = 0; i < N; i++) {
            for (int j = 0; j < N; j++) {
                if (grid[i][j] == 1) {
                    dfs(grid, i, j, colorId);
                    res = Math.max(res, size);
                    map.put(colorId, size);
                    size = 0;
                    colorId++;    
                }
            }
        }

        for (int i = 0; i < N; i++) {
            for (int j = 0; j < N; j++) {
                if (grid[i][j] == 0) {
                    int landSize = 1;
                    Set<Integer> seen = new HashSet<>();
                    for (int[] d : dir) {
                        int x = d[0] + i, y = d[1] + j;
                        if (x >= N || x < 0 || y >= N || y < 0) {continue;}
                        if (map.get(grid[x][y]) != null && !seen.contains(grid[x][y])) {
                            seen.add(grid[x][y]);
                            landSize += map.get(grid[x][y]);
                        }
                    }
                    res = Math.max(res, landSize);
                }        
            }
        }
        return res;
    }
    public void dfs(int[][] grid, int i, int j, int colorId) {
        if (i < 0 || j < 0 || i >= N || j >= N || grid[i][j] != 1) {
            return;
        }
        size++;
        grid[i][j] = colorId;
        for (int[] d : dir) {
            dfs(grid, i + d[0], j + d[1], colorId);
        }

    }
}
