```
class Solution {
public:
    int dx[4]={-1,1,0,0};
    int dy[4]={0,0,-1,1};
    int n,m;
    bool isvalid(int x,int y,vector<vector<bool>>& visited,vector<vector<int>>& grid){
        if(x>=0 and x<n and y>=0 and y<m and grid[x][y]==1 and visited[x][y]==false){
            return true;
        }
        return false;
    }
    int dfs(int x,int y,vector<vector<bool>>& visited,vector<vector<int>>& island,int islandnumber,vector<vector<int>>& grid,int& ans){
        visited[x][y]=true;
        island[x][y]=islandnumber;
        ans++;
        for(int i=0;i<4;i++){
            int new_x=x+dx[i],new_y=y+dy[i];
            if(isvalid(new_x,new_y,visited,grid)){
              dfs(new_x,new_y,visited,island,islandnumber,grid,ans);    
            }
        }
        return ans;

    }
    int largestIsland(vector<vector<int>>& grid) {
        n=grid.size();
         m=grid[0].size();
        int res=1;
        vector<vector<bool>> visited(n,vector<bool> (m,false));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                visited[i][j]=false;
            }
        }
        vector<vector<int>> island(n,vector<int>(m,0));
          for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                island[i][j]=0;
            }
        }
         int islandnumber=1,zeroes=0;;
        int ans;
        unordered_map<int,int> mp;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j]==1 and visited[i][j]==false){
                    ans=0;
                    int temp=dfs(i,j,visited,island,islandnumber,grid,ans);
                    mp[islandnumber]=temp;
                    islandnumber++;
                }
                else if(grid[i][j]==0)
                {
                    zeroes++;
                }
            }
        }
        if(zeroes==0){
            return n*m;
        }
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j]==0){
                    int temp=1;
                     unordered_set<int> st;
                    for(int k=0;k<4;k++){
                        int new_x=i+dx[k],new_y=j+dy[k];
                        if(new_x>=0 and new_x<n and new_y>=0 and new_y<m and grid[new_x][new_y]==1){
                            int ok=island[new_x][new_y];//ok stands for the island number of that 1
                            if(st.find(ok)==st.end()){ //if suppose the down 1 and right side 1 belong to the same island,we have to prevent overcounting
                                temp+=mp[ok];
                                st.insert(ok);
                            }
                        }
                    }
                    res=max(res,temp);
                }
            }
        }
        return res;
    }
};
