        for(int i=0;i<grid.length;i++){
            for(int j=0;j<grid[0].length;j++){
                if(grid[i][j]==0){
                    grid[i][j]=1;
                    max=Math.max(max,dfs(grid,i,j,new boolean[grid.length][grid[0].length]));
                    grid[i][j]=0;
                }
            }
        }
