class Solution {
    private int R;
    private int C;
    private int[] dx = {-1,0,0,1};
    private int[] dy = {0,-1,1,0};
    public int largestIsland(int[][] grid) {
        this.R = grid.length;
        this.C = grid[0].length;

        int max = 0;
        int id = 2;
        Map<Integer, Integer> area = new HashMap<>();
        area.put(0, 0); // We'll need this for later, when we're testing the 0s -> 1
        for (int row = 0; row < R; row++) {
            for (int col = 0; col < C; col++) {
                if (grid[row][col] == 1) {
                    int islandArea = bfs(grid, row, col, id);
                    area.put(id, islandArea);
                    max = Math.max(max, islandArea); // Just in case all gird spots are 1s.
                    id++;
                }
            }
        }

        // Now we need to check all the 0s, and look in the 4 directions, add up the total areas + 1 and update the max
        for (int row = 0; row < R; row++) {
            for (int col = 0; col < C; col++) {
                if (grid[row][col] == 0) {
                    int sum = 1;
                    Set<Integer> idSeen = new HashSet<>();
                    for (int i = 0; i < dx.length; i++) {
                        int newRow = row + dx[i];
                        int newCol = col + dy[i];
                        if (isInbounds(newRow, newCol) && !idSeen.contains(grid[newRow][newCol])) {
                            idSeen.add(grid[newRow][newCol]);
                            sum += area.get(grid[newRow][newCol]);
                        }
                    }

                    max = Math.max(max, sum);
                }
            }
        }

        return max;
    }

    // Count the current islands area, and mark each spot with the ID
    private int bfs(int[][] grid, int x, int y, int id) {
        Queue<int[]> queue = new ArrayDeque<>();
        int area = 0;
        queue.add(new int[]{x,y});
        grid[x][y] = id;
        while (!queue.isEmpty()) {
            area++;
            int row = queue.peek()[0];
            int col = queue.poll()[1];
            for (int i = 0; i < dx.length; i++) {
                int newRow = row + dx[i];
                int newCol = col + dy[i];
                if (isInbounds(newRow, newCol) && grid[newRow][newCol] == 1) {
                    grid[newRow][newCol] = id;
                    queue.add(new int[]{newRow, newCol});
                }
            }
        }

        return area;
    }

    private boolean isInbounds(int row, int col) {
        return (row >= 0 && row < R && col >= 0 && col < C);
    }
}

