        int area = 1;
        visited[i][j] = true;   //mark true if visited

        if(i-1 >= 0 and G[i-1][j] == 1 and !visited[i-1][j])
            area += calcArea(G, visited, i-1, j, n);

        if(i+1 < n and G[i+1][j] == 1 and !visited[i+1][j])
            area += calcArea(G, visited, i+1, j, n);

        if(j-1 >= 0 and G[i][j-1] == 1 and !visited[i][j-1])
            area += calcArea(G, visited, i, j-1, n);

        if(j+1 < n and G[i][j+1] == 1 and !visited[i][j+1])
            area += calcArea(G, visited, i, j+1, n);

        return area;
    }

    int largestIsland(vector<vector<int>>& grid) {

        int n = grid.size();    //size of the matrix
        vector<vector<bool>> visited(n, vector<bool>(n, false));   //mark the mark the island whether visited earlier or not
        map<int,int> mp;    //map between island id and area of that island       
        int maxArea = 0;    //maximum area ever counted      
        int id = 2;   //next id of the island to be assigned

        for(int i=0; i<n; i++)
        {
            for(int j=0; j<n; j++)
            if(grid[i][j] == 1)
            {
                int area = calcArea(grid, visited, i, j, n);
                maxArea = max(maxArea, area);
                mark(grid, i, j, n, id);

                mp.insert({id,area});  //mapping id and area of island

                id++;
            }
        }

        for(int i=0; i<n; i++)
        {
            for(int j=0; j<n; j++)
            {
                if(grid[i][j] == 0)             
                {
                    set<int> diffIsland; //stores the id's of different neighbour island 
                    int tArea = 1;

                    if(i+1 < n)
                        diffIsland.insert(grid[i+1][j]);
                    if(i-1 >= 0)
                        diffIsland.insert(grid[i-1][j]);
                    if(j+1 < n)
                        diffIsland.insert(grid[i][j+1]);
                    if(j-1 >= 0)
                        diffIsland.insert(grid[i][j-1]);

                    // adding areas of neighbour islands
                    for(int it : diffIsland)
                        tArea += mp[it];

                    maxArea = max(maxArea, tArea);
                }
            }
        }

        return maxArea;
    }
};
