class Solution {
    public int largestIsland(int[][] grid) {

        Map<Integer, Integer> map = new HashMap<>();
        map.put(0,0);
        int n = grid.length;
        int gridId = 2;
        // Task 1 assign id and value for each grid(1)
        for(int i=0; i<n; i++){
            for(int j=0; j<n; j++){
                if(grid[i][j]==1){
                    int size = assignIdandReturnArea(grid, i, j, gridId);
                    map.put(gridId,size);
                    gridId++;
                }   
            }
        }

        int res = map.getOrDefault(2,0);

        for(int i=0; i<n;i++){
            for(int j=0; j<n; j++){
                if(grid[i][j]==0){
                    // we will use set to avoid adding isLand with same id
                    Set<Integer> set = new HashSet<>();

                    set.add(i>0?grid[i-1][j]:0); // top id
                    set.add(i<grid.length-1?grid[i+1][j]:0); // bottom id
                    set.add(j>0?grid[i][j-1]:0); // left id
                    set.add(j<grid[0].length-1?grid[i][j+1]:0); // right id

                    int totalArea = 1; // we need to count the current island as well
                    for(int id:set){
                        totalArea += map.get(id); 
                    }
                    res = Math.max(res, totalArea);
                }
            }
        }
     return res;     
    }

    private int assignIdandReturnArea(int[][] grid, int i, int j, int id){
        if(i<0 || j<0 || j>=grid[0].length || i>=grid.length || grid[i][j]!=1) return 0;

        grid[i][j] = id;

        return 1 + assignIdandReturnArea(grid, i-1, j, id) + assignIdandReturnArea(grid, i+1, j, id) +
            assignIdandReturnArea(grid, i, j-1, id) + assignIdandReturnArea(grid, i, j+1, id);

    }
}
