class DisjointSet {
public:
    vector<int> rank, parent, size;
	DisjointSet(int n) {
		rank.resize(n + 1, 0);
		size.resize(n + 1);
		parent.resize(n + 1);

		for (int i = 0; i < n; i++) {
			parent[i] = i;
			size[i] = 1;
		}
	}

	// finding ultimate parent
	int findUltimateParent(int node) {
		if (node == parent[node])
			return node;
		return parent[node] = findUltimateParent(parent[node]);
	}

	void unionByRank(int u, int v) {
		int UltimateParent_u = findUltimateParent(u);
		int UltimateParent_v = findUltimateParent(v);

		if (UltimateParent_u == UltimateParent_v)
			return;

		if (rank[UltimateParent_u] < rank[UltimateParent_v])
			parent[UltimateParent_u] = UltimateParent_v;
		else if (rank[UltimateParent_v] < rank[UltimateParent_u])
			parent[UltimateParent_v] = UltimateParent_u;
		else {
			parent[UltimateParent_v] = UltimateParent_u;
			rank[UltimateParent_u]++;
		}
	}

	void unionBySize(int u, int v) {
		int UltimateParent_u = findUltimateParent(u);
		int UltimateParent_v = findUltimateParent(v);

		if (UltimateParent_u == UltimateParent_v)
			return;

		if (rank[UltimateParent_u] < rank[UltimateParent_v]) {
			parent[UltimateParent_u] = UltimateParent_v;
			size[UltimateParent_v] += size[UltimateParent_u];
		} else {
			parent[UltimateParent_v] = UltimateParent_u;
			size[UltimateParent_u] += size[UltimateParent_v];
		}
	}
};

class Solution {
public:
    int largestIsland(vector<vector<int>>& grid) {
        int n = grid.size();
        DisjointSet ds(n*n);

        // Connecting components -> step-1
        for(int row=0;row<n;row++) {
            for(int col=0;col<n;col++) {
                if(grid[row][col] == 0)
                    continue;
                int delrow[] = {-1, 0, 1, 0};
                int delcol[] = {0, -1, 0, 1};

                for(int i=0;i<4;i++) {
                    int nrow = row + delrow[i];
                    int ncol = col + delcol[i];
                    if(nrow >= 0 && nrow < n && ncol >= 0 && ncol < n) {
                        if(grid[nrow][ncol] == 1) {
                            int nodeNumber = row * n + col;
                            int adjNodeNumber = nrow * n + ncol;
                            ds.unionBySize(nodeNumber, adjNodeNumber);
                        }
                    }
                }
            }
        }

        // Try Changing 0 to 1 -> step-2
        int maxi = 0;
        for(int row=0;row<n;row++) {
            for(int col=0;col<n;col++) {
                if(grid[row][col] == 1)
                    continue;
                int delrow[] = {-1, 0, 1, 0};
                int delcol[] = {0, -1, 0, 1};

                set<int> components;
                for(int i=0;i<4;i++) {
                    int nrow = row + delrow[i];
                    int ncol = col + delcol[i];

                    if(nrow >= 0 && nrow < n && ncol >= 0 && ncol < n) {
                        if(grid[nrow][ncol] == 1) {
                            components.insert(ds.findUltimateParent(nrow * n + ncol));       
                        }
                    }
                }
                int totalSize = 0;
                for(auto it: components) {
                    totalSize += ds.size[it];
                }
                maxi = max(maxi, totalSize + 1);
            }
        }

        for(int cellNumber = 0; cellNumber < n * n; cellNumber++) {
            maxi = max(maxi, ds.size[ds.findUltimateParent(cellNumber)]);
        }
        return maxi;
    }
};
