class DSU {
    int[] parent;
    int[] size;
    int max;

    public DSU(int n) {
        parent = new int[n];
        size = new int[n];
        max = 0;
        for (int i = 0; i < n; i++) {
            parent[i] = -1;
            size[i] = 1;
        }
    }


    public int biggest() {
        return max;
    }

    public int find(int x) {
        if (parent[x] != x)
            parent[x] = find(parent[x]);
        return parent[x];
    }

    public void addIsland(int x) {
        if (parent[x] >= 0)
            return;
        parent[x] = x;
        max=Math.max(max,1);
    }

    public void union(int x, int y) {
        int px = find(x), py = find(y);

        if (px != py) {
            if (size[px] >= size[py]) {
                size[px] += size[py];
                parent[py] = px;
                max = Math.max(max, size[px]);
            } else {
                size[py] += size[px];
                parent[px] = py;
                max = Math.max(max, size[py]);
            }
        }
    }
      public int getS(int x) {
          int px= find(x);
        return size[px];
    }

    public boolean check(int x){
        if (parent[x] >= 0) {
            return true;
        } else {
            return false;
        }
    }
}

class Solution {
    public int largestIsland(int[][] grid) {
        int n = grid.length;
        DSU set = new DSU(n * n);
        int drow[] = {-1, 1, 0, 0};
        int dcol[] = {0, 0, -1, 1};

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == 1) {
                    set.addIsland(i*n+j);
                    for (int k = 0; k < 4; k++) {
                        int dr = i + drow[k];
                        int dc = j + dcol[k];
                        int child = n * dr + dc;
                        if (dr >= 0 && dr < n && dc >= 0 && dc < n)
                           if(set.check(dr*n+dc))
                            set.union(i * n + j, child);
                    }
                }
            }
        } 
         int res=set.biggest();
         for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {

                if (grid[i][j] == 0) {                    
                    int temp=1;
                    Set<Integer> hs= new HashSet<>();
                    for (int k = 0; k < 4; k++) {
                        int dr = i + drow[k];
                        int dc = j + dcol[k];
                        int child = n * dr + dc;
                        if (dr >= 0 && dr < n && dc >= 0 && dc < n)
                           if(set.check(dr*n+dc)){
                              if( hs.add(set.find(child))){
                                    temp+=set.getS(child);
                              }

                           }                             
                    }
                res= Math.max(res,temp);
                }

            }
        }

       return res;
    }
}

