class UnionFind{
    constructor(size){
        this.root = new Array(size);
        this.rank = new Array(size);
        for(let i = 0; i < size; i++){
            this.root[i] = i;
            this.rank[i] = 1;
        }
    }
    find(x){
        if(this.root[x] === x){
            return x;
        }else{
            let y = this.root[x];
            while(y !== this.root[y]){
                y = this.root[y];
            }
            this.root[x] = y;
            return this.root[x];
        }
    }
    union(x, y){
        let rootX = this.find(x);
        let rootY = this.find(y);
        if(rootX !== rootY){
            if(this.rank[rootY] < this.rank[rootX]){
                this.root[rootY] = rootX;
            }else if(this.rank[rootX] < this.rank[rootY]){
                this.root[rootX] = rootY;
            }else{
                this.root[rootY] = rootX;
                this.rank[rootX] += 1;
            }
        }
    }
}

/**
 *
 */
function sortingFun(a, b){
    if(a > b){
        return 1;
    }else if(a < b){
        return -1;
    }else{
        return 0;
    }
}

/**
 * Let N be the length of string s.
 * Time: O(N^2lgN)
 * Space: O(3N) => O(N)
 * @param {string} s
 * @param {number[][]} pairs
 * @return {string}
 */
var smallestStringWithSwaps = function(s, pairs) {
    // 1. Create disjoint sets of the all swappable indices.
    // T:O(N^2)
    let uf = new UnionFind(s.length);
    for(let [u, v] of pairs){
        uf.union(u,v);
    }

    // 2. Create an array of the swappable set of indices, with the index as the root number from the union find root array.
    // [list1,list2,...]
    // T: O(NlgN)
    let indexSwapSet = new Array(s.length);
    for(let i = 0; i < s.length; i++){
        if( i !== uf.root[i]){
            // Make sure the roots are compressed.
            uf.find(i);     // O(lgN)
        }
        let index = uf.root[i];
        if(indexSwapSet[index] === undefined){
            // If index location is not initialized.
            indexSwapSet[index] = [];
        }
        indexSwapSet[index].push(i);
    }

    // 3. Create a char array where it will be s modified with all the swapping in order to have the most lexicographically order we can get.
    // O(N^2lgN)
    let sSwap = new Array(s.length);
    for(let i = 0; i < s.length; i++){
        // Go through each set of swappable indices.
        if(indexSwapSet[i] !== undefined){
            // a. Create a char array with with a character in s corresponding to each index in the current set of indices we are in.
            let charSwapSet = [];
            for(let index of indexSwapSet[i]){
                charSwapSet.push(s[index]);
            }
            // b. Now sort the array.
            charSwapSet.sort(sortingFun);
            for(let j = 0; j < charSwapSet.length; j++){
                // c. Do the swap in our new char array.
                sSwap[indexSwapSet[i][j]] = charSwapSet[j]; 
            }
        }
    }

    // Return char array transformed into a string.
    return sSwap.join('');
};
