function smallestStringWithSwaps(s, pairs) {
    const unionFind = new UnionFind(s.length)
    pairs.forEach(([v1, v2]) => unionFind.union(v1, v2));

    const result = [];
    unionFind.disjointSets().forEach((idxs, root) => {
        const chars = idxs.map(idx => s[idx]);
        chars.sort();
        let i = 0;
        idxs.forEach(idx => {
            result[idx] = chars[i++];
        })
