public String smallestStringWithSwaps(String s, List<List<Integer>> pairs) {
        UnionFind unionFind = new UnionFind(s.length());
        pairs.forEach(pair -> unionFind.union(pair.get(0), pair.get(1)));

        return createSmallestString(s, createComponents(s, unionFind), unionFind);
    }

    private Map<Integer, PriorityQueue<Character>> createComponents(String s, UnionFind unionFind) {
        Map<Integer, PriorityQueue<Character>> components = new HashMap<>();
        for (int vertex = 0; vertex < s.length(); ++vertex) {
            int root = unionFind.find(vertex);
            components.putIfAbsent(root, new PriorityQueue<>());
            components.get(root).offer(s.charAt(vertex));
        }
        return components;
    }

    private String createSmallestString(String s, Map<Integer, PriorityQueue<Character>> components, UnionFind unionFind) {
        StringBuilder smallest = new StringBuilder();
        for (int i = 0; i < s.length(); i++) {
            smallest.append(components.get(unionFind.find(i)).poll());
        }
        return smallest.toString();
    }

    public static class UnionFind {
        private final int[] root;

        public UnionFind(int count) {
            this.root = new int[count];
            for (int i = 0; i < count; ++i) {
                root[i] = i;
            }
        }

        public int find(int node) {
            if (root[node] == node)
                return node;
            return root[node] = find(root[node]);
        }

        public void union(int node1, int node2) {
            int root1 = find(node1);
            int root2 = find(node2);
            if (root1 >= root2)
                root[root2] = root1;
            else
                root[root1] = root2;
        }
    }
