class Solution:
    def smallestStringWithSwaps(self, s: str, pairs: List[List[int]]) -> str:
        class UnionFind:
            def __init__(self,size):
                self.root=list(range(size))
            def find(self,x):
                if self.root[x]==x:
                    return x
                self.root[x]=self.find(self.root[x])
                return self.root[x]
            def union(self,x,y):
                root_x=self.find(x)
                root_y=self.find(y)
                if root_x!=root_y:
                    self.root[root_x]=root_y
        union_find=UnionFind(len(s))
		# Step 1&2: Connect pairs into components
        for pair in pairs:
            union_find.union(pair[0],pair[1])
        component=defaultdict(list)
		#Step 3: Add character into it's root component
        for i in range(len(s)):
            component[union_find.find(i)].append(s[i])
		#Step 4:Sort each component
        for key in component:
            component[key].sort(reverse=True)
		#Step 5:Take the smallest character from component find(i) into the i'th index
        return "".join([component[union_find.find(i)].pop() for i in range(len(s))])
