class Solution:
    def smallestStringWithSwaps(self, s: str, pairs: List[List[int]]) -> str:
        n = len(s)
        G = [[] for _ in range(n)]
        for i,j in pairs:
            G[i].append(j)
            G[j].append(i)

        seen = [False]*n
        def dfs(i, indices):
            indices.append((i,s[i]))
            seen[i] = True
            for j in G[i]:
                if not seen[j]:
                    dfs(j, indices)

        ans = [None]*n
        for i in range(n):
            if not seen[i]:
                indices = []
                dfs(i, indices)
                if not indices:
                    ans[i] = s[i]
                else:
                    K = sorted(indices, key=lambda x:x[1])
                    P = sorted(indices, key=lambda x:x[0])
                    for i in range(len(indices)):
                        ans[P[i][0]] = K[i][1]

        return "".join(ans)
