from collections import defaultdict
class Solution:
    def smallestStringWithSwaps(self, s: str, pairs: List[List[int]]) -> str:
        if not pairs:
            return s

        class UnionFind:
            def __init__(self, n):
                self.n = n
                self.sz = [1] * n
                self.gp = list(range(n))

            def find(self, x):
                xi = x
                if self.gp[x] != x:
                    xi = self.find(self.gp[x])
                    # path compression
                    self.gp[x] = xi
                return xi

            def union(self, x, y):
                xi = self.find(x)
                yi = self.find(y)
                if xi != yi: 
                    # tree balancing
                    p, c = (xi, yi) if self.sz[xi] > self.sz[yi] else (yi, xi)
                    self.gp[c] = p 
                    self.sz[p] += self.sz[c]

            def __repr__(self):
                return "{}\n{}\n{}".format(list(range(len(self.gp))), self.gp, self.sz)

            def getGroups(self):
                groups = defaultdict(list)
                for i in range(self.n):
                    groups[self.find(i)] += [i]
                return groups.values()

		# Use union find to build up groups
        uf = UnionFind(len(s))
        for x, y in pairs:
            uf.union(x, y)

		# Modify the string
        s = list(s)
        for g in uf.getGroups():
            for i, c in zip(sorted(g), sorted(s[i] for i in g)):
                s[i] = c
        return ''.join(s)
