class disjointset{
    public:
    vector<int>par;
    disjointset(int n){
        par.resize(n+1);
        for(int i=0; i<n; i++){
            par[i]=i;
        }
    }
    int findp(int u){
        if(u==par[u]){
            return u;
        }
        return par[u]=findp(par[u]);
    }
    void un(int u, int v){
        if(findp(u)==findp(v)){return;}
        par[findp(u)]=par[findp(v)];
    }
};
class Solution {
public:
    string smallestStringWithSwaps(string s, vector<vector<int>>& pairs) {
        map<int, vector<int>>m;
        disjointset d(s.size());
        for(auto it: pairs){
            d.un(it[0], it[1]);
