        for(auto it: m){
            vector<int>v=it.second;
            string ans="";
            for(auto it1: v){
                ans+=s[it1];
            }
            sort(ans.begin(), ans.end());
            for(int i=0; i<ans.size(); i++){
                s[v[i]]=ans[i];
            }
        }
        return s;
    }
};
