string S;

bool cmp(int a, int b){
    return S[a] < S[b];
}
class Solution {
public:
    int dsu[100001];
    void change(int a, int b){
        if(dsu[a] == a){
            dsu[a] = b;
            return;
        }
        change(dsu[a], b);
    }

    int find(int a){
        if(dsu[a] == a) return a;
        return dsu[a] = find(dsu[a]);
    }

    int parent(int a){
        if(dsu[a] == a) return a;
        return parent(dsu[a]);
    }

    string smallestStringWithSwaps(string s, vector<vector<int>>& pairs) {
        int n = s.size();
        for(int i=0;i<n;i++) dsu[i] = i;
        string ans = s;
        S = s;

        for(auto&it: pairs){
            int a = parent(it[0]);
            int b = parent(it[1]);
            if(a != b){
                change(it[0], min(a, b));
                change(it[1], min(a, b));
            }
        }

        for(int i=1;i<n;i++){
            dsu[i] = find(i);
        }

        vector<vector<int>> ch(n);

        for(int i=0;i<n;i++){
            ch[dsu[i]].push_back(i);
        }

        for(int i=0;i<n;i++){
            if(ch[i].size() != 0){
                vector<int> temp = ch[i];
                sort(ch[i].begin(), ch[i].end(), cmp);
                for(int j=0;j<temp.size();j++){
                    ans[temp[j]] = s[ch[i][j]];
                }
            }
        }

        return ans;

    }
};
