class DSU {
    vector<int> parents;
    vector<int> rank;
    int n;
public:
    DSU(int n) {
        this->n = n;
        parents.resize(n);
        rank.resize(n, 0);
        for (int i = 0; i < n; ++i) {
            parents[i] = i;
        }
    }

    int find(int x) {
        if (parents[x] != x) {
            parents[x] = find(parents[x]);
        }
        return parents[x];
    }

    bool unionize(int a, int b) {
        int pa = find(a);
        int pb = find(b);

        if (pa==pb) return false;

        if (rank[pb] < rank[pa]) {
            parents[pb] = pa;
            rank[pa] += rank[pb];
        } else {
            parents[pa] = pb;
            rank[pb] += rank[pa];
        }
        return true;
    }
};

class Solution {
public:
    string smallestStringWithSwaps(string s, vector<vector<int>>& pairs) {
        int n = s.length();

        vector<int> parents(n, 0);

        for (int i = 0; i < n; i++) {
            parents[i] = i;
        }

        DSU d(n);

        for (auto pair : pairs) {
            d.unionize(pair[0], pair[1]);
        }

        unordered_map<int,vector<int>> components;
        for (int i = 0; i < n; ++i) {
            components[d.find(i)].push_back(i);
        }

        for (auto comp : components) {
            string curstr="";
            for (auto idx : comp.second) {
                curstr += s[idx];
            }

            sort(curstr.begin(), curstr.end());

            for (int i = 0; i < comp.second.size(); ++i) {
                s[comp.second[i]] = curstr[i];
            }
        }

        return s;
    }
};
