All of letters that connect each other can swap, so we need to find all unions and sort those unions. After sorting we merge all sorted unions to the result.
My code uses disjoin set to union graph and use min-heap to sort them.
```
int p[100000];
int N;

class Solution {
public:

    int find(int x, int y) {
        if (p[x] == x and p[y] == y) {
            if (x < y) {
                p[y] = x;
                return x;
            } else {
                p[x] = y;
                return y;
            }
        } else {
            int newP = find(p[x], p[y]);
            p[x] = newP;
            p[y] = newP;
            return newP;
        }
    }

    int findParent(int x) {
        if (p[x] == x) return x;
        p[x] = findParent(p[x]);
        return p[x];
    }


    void unionFind(vector<vector<int>>& pairs) {
        for (int i = 0; i < pairs.size(); i++) {
            find(pairs[i][0], pairs[i][1]);
        }

        for (int i = 0; i < N; i++) {
            if (p[i] != i) findParent(i);
        }
    }


    string smallestStringWithSwaps(string s, vector<vector<int>>& pairs) {
        N = s.size();
        for (int i = 0; i < N; i++) {
            p[i] = i;
        }
        // Step 1: Union Find
        unionFind(pairs);
        // Step 2: Using MinHeap to sort string.
        unordered_map<int, priority_queue<int, vector<int>, greater<int>>> vq;

        for (int i = 0; i < N; i++) {
            vq[p[i]].push(s[i]);
        }

        // Step 3: Merge all sorted min heaps to result.
        string res = "";
        for (int i = 0; i < N; i++) {
            res += vq[p[i]].top();
            vq[p[i]].pop();
        }

        return res;
    }
};
```

