        num_s = len(s)
        unionfind = UnionFind(num_s)
        for i, (x, y) in enumerate(pairs):
            unionfind.union(x, y)

        from collections import defaultdict
        lexi = defaultdict(list)

        for i in range(num_s):
            unionfind.find(i)
            cur_root = unionfind.root[i]
            lexi[cur_root].append(s[i])

        for key, item in lexi.items():
            item.sort(reverse=True)

        ans = ""
        for i in range(num_s):
            cur_root = unionfind.root[i]
            ans += lexi[cur_root].pop()

        return ans      
