class Solution {
    public String smallestStringWithSwaps(String s, List<List<Integer>> pairs) {
        int n = s.length();
        UnionFind uf = new UnionFind(n);

        for(List<Integer> pair:pairs){
            uf.union(pair.get(0),pair.get(1));
        }

        Map<Integer,Queue<Character>> map = new HashMap<>();
        for(int i=0;i<s.length();i++){
            char c = s.charAt(i);
            int p = uf.find(i);
            if(!map.containsKey(p))map.put(p,new PriorityQueue<>());
            map.get(p).offer(c);
        }

        StringBuilder sb = new StringBuilder();
        for(int i=0;i<n;i++){
            int p = uf.find(i);
            char c = map.get(p).poll();
            sb.append(c);
        }
        return sb.toString();
    }

    class UnionFind{
        int[]parent;

        UnionFind(int n){
            parent = new int[n];
            for(int i=0;i<n;i++){
                parent[i] = i;
            }
        }

        boolean union(int n1, int n2){
            int p1 = find(n1);
            int p2 = find(n2);
            if(p1 == p2)return false;
            parent[p2] = p1;
            return true;
        }

        int find(int n){
            if(n==parent[n])return n;
            return parent[n]=find(parent[n]);
        }
    }
}
