# ==== UnionFind ====
class UnionFind:
    def __init__(self, size):
        self.root = [i for i in range(size)]

    def find(self, x):
        if self.root[x] != x:
            self.root[x] = self.find(self.root[x])
        return self.root[x]

    def union(self, x, y):
        rootX = self.find(x)
        rootY = self.find(y)
        if rootX != rootY:
            self.root[rootY] = rootX

class Solution(object):
    def smallestStringWithSwaps(self, s, pairs):
        """
        :type s: str
        :type pairs: List[List[int]]
        :rtype: str
        """
        # construct the unionfind graph
        uf = UnionFind(len(s))
        for x, y in pairs:
            uf.union(x, y)

        # a dictionary to store indexes of unique sub graphs
        # key is root, value is list of connected nodes
        indexes = collections.defaultdict(list)
        for i, ind in enumerate(uf.root):
            root = uf.find(ind)
            indexes[root].append(i)

        result = list(s) 
        # for each root, sort the connected nodes' indexes and chars, then output by pairs
        for root in indexes.keys():
            sub_index = indexes[root]
            sub_alpha = [s[i] for i in sub_index]
            sub_index.sort()
            sub_alpha.sort()
            for i, a in zip(sub_index, sub_alpha):
                result[i] = a

        return ''.join(result)
```

