from collections import defaultdict

class Solution:
    def smallestStringWithSwaps(self, s: str, pairs: List[List[int]]) -> str:
        if not pairs:
            return s

        parent = [-1] * len(s)

        def getgroup(node):
            if parent[node] == -1:
                return node
            return getgroup(parent[node])

        def union(a, b):
            nonlocal group
            ag = getgroup(a)
            bg = getgroup(b)

            if ag == bg:
                return ag

            if ag < bg:
                parent[bg] = ag
                group[ag] |= group[bg]
                return ag
            else:
                parent[ag]  = bg
                group[bg] |= group[ag]
                return bg


        group = defaultdict(set)

        for i, j in pairs:
            g = union(i, j)
            group[g].add((s[i], i))
            group[g].add((s[j], j))


        for k, lst in group.items():
            group[k] = sorted(lst, reverse=True)

        a = [''] * len(s)
        for i in range(len(s)):
            g = group[getgroup(i)]
            a[i] = g.pop()[0] if g else s[i]

        return ''.join(a)
