            int u=find(i, parent);
            parent[i]=u;
            pq=map.get(u);
            pq.offer(s.charAt(i));
        }

        StringBuilder st=new StringBuilder();
        for(int i=0; i<parent.length; i++) {
            int u=parent[i];
            pq=map.get(u);
            st.append(pq.poll());
        }

        return st.toString();
    }
