class Solution {
public:
    int find(int x , vector<int>&parent) // find function
    {
        if(x == parent[x])
            return x;
        else
            return parent[x] = find(parent[x], parent); // path compression
    }
    string smallestStringWithSwaps(string s, vector<vector<int>>& pairs) {
        int n = s.size(), m = pairs.size();
        vector<int>parent(n+1),size(n+1,1);
        for(int i = 0 ; i < n ; i++)
            parent[i]=i;

        for(int i = 0 ; i < m ; i++) // Traverse all pairs and make components/ groups/ sets.
        {
            int a = find(pairs[i][0],parent);
            int b = find(pairs[i][1],parent);
            if(a != b)
            {
                if(size[a] < size[b]) // Union by size/rank
                    swap(a,b);
                parent[b] = a;
                size[a] += size[b];
            }
        }
        unordered_map<int,vector<int>>mp; // make a map to store all indexes of a root index.
        for(int i = 0 ; i < n ; i++)
            mp[find(i,parent)].push_back(i);
			 /*
			 Traverse all stored indexes and form a substrings by collecting the characters at the 
			 indexes, and since it is mentioned that we can swap the pairs any number of times,
			 thus all characters in a component can be sorted. Thus, sort them and modify the
			 original string.
			*/
		for(auto x : mp)
		{
            string str = ""; int j = 0 ;
            for(auto i : x.second)
                str += s[i];
            sort(str.begin(), str.end());
            for(auto i : x.second)
                s[i] = str[j++];
        }
        return s;
    }
};
