class Solution {
    func smallestStringWithSwaps(_ s: String, _ pairs: [[Int]]) -> String {
        let count = s.count
        let s = Array(s)
        var dsu = DSU(count)
        for i in pairs {
            dsu.union(i[0], i[1])
        }
        var dict = [Int: [Int]]()
        for i in 0..<count {
            let key = dsu.find(i)
            dict[key, default: []].append(i)
        }

        var res = s

        for (key, value) in dict {
            let sortedChar = value.map { s[$0] }.sorted()
            for (i, j) in zip(value, sortedChar) {
                res[i] = j
            }
        }

        return String(res)
    }
}

struct DSU {
    var parent: [Int]
    var rank: [Int]
    var size: Int {
        return rank.max() ?? 0
    }

    init(_ n: Int) {
        self.parent = Array(0..<n)
        self.rank = Array(repeating: 1, count: n)
         parent = [Int](0..<n)
         rank = [Int](repeating: 0, count: n)
    }

    mutating func find(_ a: Int) -> Int {
        if a == parent[a] {
            return a
        }
        parent[a] = find(parent[a])
        return parent[a]
    }

    mutating func union(_ a: Int, _ b: Int) -> Bool {
        var i = find(a)
        var j = find(b)
        if i != j {
            if rank[i] < rank[j] {
                var temp = i
                i = j
                j = temp
            }

            parent[j] = parent[i]
            rank[j] += rank[i]
            rank[i] = rank[j]
            return true
        }

        return false
    }
}
