        memo = [i for i in range(n)]

        def find(x):
            if x != memo[x]:
                memo[x] = find(memo[x])
            return memo[x]

        def union(x,y):
            rx, ry = find(x), find(y)
            if rx != ry:
                memo[rx] = ry

        for i,j in pairs:
            union(i,j)

        group = collections.defaultdict(list)
        for i in range(n):
            group[find(i)] += s[i],

        for j in group.values():
            j.sort()

        si = collections.Counter()
        ans = ["" for _ in range(n)]
        for i in range(n):
            ans[i] = group[find(i)][si[find(i)]]
            si[find(i)] += 1
        return "".join(ans)

