class DSU:
    def __init__(self, N):
        self.parent = [i for i in range(N)]
        self.size = [1 for _ in range(N)]
        self.components = N

    def find(self, x):
        if x != self.parent[x]:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, a, b):
        ua = self.find(a)
        ub = self.find(b)
        if ua == ub:
            return False

        if self.size[ua] < self.size[ub]:
            ua, ub = ub, ua
        self.parent[ub] = ua
        self.size[ua] += self.size[ub]
        self.size[ub] = self.size[ua]
        self.components -= 1        
        return True

from sortedcontainers import SortedList
class Solution:
    def smallestStringWithSwaps(self, s: str, pairs: List[List[int]]) -> str:
        N = len(s)
        uf = DSU(N)
        for u, v in pairs:
            uf.union(u, v)

        lookup = defaultdict(SortedList)
        for i, node in enumerate(uf.parent):
            lookup[uf.find(node)].add(s[i])

        ans = []
        for i in range(N):
            ans.append(lookup[uf.find(i)].pop(0))
        return ''.join(ans)
    # Time: O((M + N) * α(N) + N * log(N)) where M is the length of pairs and N the length of s
    # Space: O(N)
