class Solution {
    public String smallestStringWithSwaps(String s, List<List<Integer>> pairs) {
        int n = s.length();
        UnionFind uf = new UnionFind(n);
        for (List<Integer> edge : pairs)
            uf.union(edge.get(0), edge.get(1));

        Map<Integer, List<Integer>> rootToChildernMap = new HashMap<>();
        for (int v = 0; v < n; v++) {
            int root = uf.find(v);
            rootToChildernMap.putIfAbsent(root, new ArrayList<>());
            rootToChildernMap.get(root).add(v);
        }

        char[] result = new char[n];
        for (List<Integer> indices : rootToChildernMap.values()) {
            List<Character> chars = new ArrayList<>();
            for (int idx : indices) chars.add(s.charAt(idx));
            Collections.sort(chars);
            for (int i = 0; i < chars.size(); i++)
                result[indices.get(i)] = chars.get(i);
        }
        return new String(result);
    }

    static class UnionFind {
        int[] parent, rank;

        public UnionFind(int size) {
            parent = new int[size];
            rank = new int[size];
            for (int i = 0; i < size; i++) parent[i] = i;
        }

        private int find(int x) {
            if (parent[x] != x) parent[x] = find(parent[x]);
            return parent[x];
        }

        public boolean union(int a, int b) {
            int p1 = find(a), p2 = find(b);
            if (p1 == p2) return false;
            if (rank[p1] > rank[p2]) parent[p2] = p1;
            else if (rank[p2] > rank[p1]) parent[p1] = p2;
            else {
                parent[p2] = p1;
                rank[p1]++;
            }
            return true;
        }
    }
}
