        for(vector<int>& it : pairs) {
            UF.Union(it[0], it[1]);
        }

        // retrieve the individual components from the UnionFind data structure
        unordered_map<int,vector<pair<int,char>>> components;

        for(int i=0; i<s.size(); i++){
            components[UF.Find(i)].push_back({i,s[i]});
        }

        // string of size s.size() for holding the result
        string result = string(s.size(), 'x');

        // sort the characters present in each component
        for(pair<int,vector<pair<int,char>>> it : components){
            vector<pair<int,char>> component = it.second;

