        int x = find(i,parent);
        int y = find(j,parent);
        if(x == y) return;
        if(rank[x] > rank[y]){
            parent[y] = x; 
        }else if(rank[x] < rank[y]){
            parent[x] = y;
        }else{
            parent[x] = y;
            rank[y]++;
        }
