class UnionFind {
public:
    UnionFind(int sz) : root(sz), rank(sz) {
        for (int i = 0; i < sz; i++) {
            root[i] = i;
            rank[i] = 1;
        }
    }

    int find(int x) {
        if (x == root[x]) {
            return x;
        }
        return root[x] = find(root[x]);
    }

    void unionSet(int x, int y) {
        int rootX = find(x);
        int rootY = find(y);
        if (rootX != rootY) {
            if (rank[rootX] > rank[rootY]) {
                root[rootY] = rootX;
            } else if (rank[rootX] < rank[rootY]) {
                root[rootX] = rootY;
            } else {
                root[rootY] = rootX;
                rank[rootX] += 1;
            }
        }
    }

    bool connected(int x, int y) {
        return find(x) == find(y);
    }

private:
    vector<int> root;
    vector<int> rank;
};

class Solution {
public:
    string smallestStringWithSwaps(string s, vector<vector<int>>& pairs) {
        UnionFind uf(s.size());
        for(auto& it: pairs) uf.unionSet(it[0], it[1]);
        unordered_map<int, priority_queue<int, vector<int>, greater<int>>> check;
        for(int i = 0; i < s.size(); ++ i){
            check[uf.find(i)].push(s[i]);
        }

        string ans = "";
        for(int i = 0; i < s.size(); ++ i){
            ans += check[uf.find(i)].top();
            check[uf.find(i)].pop();
        }

        return ans;
    }
};
