class Solution {
    // customized union find
    // the count stands for how many nodes for that root
    class UnionFind {
        public int[] root;
        private int[] rank;
        public int[] count;
        // TC O(n), SC O(n)
        public UnionFind(int n) {
            this.root = new int[n];
            this.rank = new int[n];
            this.count = new int[n];
            Arrays.fill(root, -1);
            Arrays.fill(rank, 1);
            Arrays.fill(count, 1);
        }
        // TC O(a(n)), average O(1)
        public int find(int a) {
            if (root[a] == -1) return a;
