struct DSU
{
    int connected;
    vector<int> par, sz;
    void init(int n) 
    {
        par = sz = vector<int> (n + 1, 0);
        for(int i = 1; i <= n; i++)
            par[i] = i, sz[i] = 1;
        connected = n;
    }
    int getPar(int u)
    {
        while(u != par[u])
        {
            par[u] = par[par[u]];
            u = par[u];
        }
        return u;
    }
    void unite(int u, int v)
    {
        int par1 = getPar(u), par2 = getPar(v);

        if(par1 == par2)
            return;
          connected--;
        if(sz[par1] > sz[par2])
            swap(par1, par2);
        sz[par2] += sz[par1];
        sz[par1] = 0;
        par[par1] = par[par2];
    }
};
class Solution {
public:
    string smallestStringWithSwaps(string s, vector<vector<int>>& pairs) {
        int n=s.size();
        DSU d; // Disjoint set union declaration
        d.init(n); //initialization 
        for(auto i:pairs)
        {
            d.unite(i[0],i[1]); // uniting edges
        }
        vector<int>adj[n+1];
        for(int i=0;i<s.size();i++)
        {
            int x=d.getPar(i); // accumulation positions with same parents
            adj[x].emplace_back(i);
        }
        for(int i=0;i<n;i++)
        {
            if(adj[i].size()==0)
                continue;
            sort(adj[i].begin(),adj[i].end());// sorting all the positions
            string k="";
            for(auto j:adj[i])
            {
                k+=s[j];
            }
            sort(k.begin(),k.end()); // sorting all the characters
            for(int j=0;j<k.size();j++)
            {
                s[adj[i][j]]=k[j];   // distributing the characters in lexicographical increasing order
            }
        }
        return s;


    }
};
