class UnionFind {
	private int[] root;
	private int[] rank;

	public UnionFind(int size) {
		root = new int[size];
		rank = new int[size];
		for (int i = 0; i < size; i++) {
			root[i] = i;
			rank[i] = 1;
		}
	}

	public int Find(int vertex) {
		if (vertex == root[vertex]) {
			return vertex;
		}
		return root[vertex] = Find(root[vertex]);
	}
	public void Union(int vertex1, int vertex2) {
		int root1 = Find(vertex1);
		int root2 = Find(vertex2);
		if (root1 != root2) {
			if (rank[root1] >= rank[root2]) {
				root[root2] = root1;
				rank[root1] += rank[root2];
			} else {
				root[root1] = root2;
				rank[root2] += rank[root2];
			}
		}
	}
}


public class Solution {      
	public string SmallestStringWithSwaps(string s, IList<IList<int>> pairs) {
		//Do the unionFind to identify the connected swapable letters
		UnionFind uf = new UnionFind(s.Length);
		foreach(List<int> pair in pairs){
			uf.Union(pair[0],pair[1]);
		}

		Dictionary<int,List<char>> letterGroups = new Dictionary<int,List<char>>();
		//Group the swapable letters based on root
		for(int i=0;i<s.Length;i++){
			int key = uf.Find(i);
			if(!letterGroups.ContainsKey(key)){
				letterGroups[key]=new List<char>();
			}
			letterGroups[key].Add(s[i]);
		}

		//Sort the swapable letters within each group
		foreach(int key in letterGroups.Keys){
			letterGroups[key].Sort();
		}

		//Iterate through the string and remove the smallest letter from its corresponding root group
		char[] smallestString = new char[s.Length];
		for(int i=0;i<s.Length;i++){
			int key = uf.Find(i);
			smallestString[i] = letterGroups[key][0];
			letterGroups[key].RemoveAt(0);
		}
		return new String(smallestString);
	}
}

