class Solution {
public:
    #define debug(x) cout<<#x<<":"<<x<<endl;
    vector<int> parent;
    vector<int> rank;
    string smallestStringWithSwaps(string s, vector<vector<int>>& pairs) {
        int n = s.size();
        parent.resize(n,-1);
        rank.resize(n,-1);
        Union(s,pairs);
        unordered_map<int,priority_queue<char,vector<char>, greater<char>>> umap;
        for(int i = 0;i<n;i++){
            int parentId = Find(i);
            umap[parentId].push(s[i]);
        }
        for(int i=0;i<n;i++){
            int parentId = Find(i);
            char ch = umap[parentId].top();umap[parentId].pop();
            s[i] = ch; 
        }
        return s;
    }

    void Union(string& s,vector<vector<int>>& pairs){
        for(auto& edge:pairs){
            int srcParent = Find(edge[0]);
            int destParent = Find(edge[1]);
            if(rank[srcParent] > rank[destParent]){
                parent[destParent] = srcParent;
            }else if(rank[destParent] > rank[srcParent]){
                parent[srcParent] = destParent;
            }else{
                parent[srcParent] = destParent;
                rank[srcParent]++;
            }
        }
    }

    int Find(int id){
        if(parent[id] == -1) return parent[id] = id;
        int src = id;
        while(id != parent[id] && parent[id] != -1) id = parent[id];
        return parent[src] = id;
    }

};
