class UnionFind:
    def __init__(self, size):
        self.root = [i for i in range(size)]
        self.rank = [1] * size

    def find(self, x):
        if x == self.root[x]:
            return x

        self.root[x] = self.find(self.root[x])

        return self.root[x]

    def union(self, x, y):
        root_x = self.find(x)
        root_y = self.find(y)

        if root_x != root_y:
            if self.rank[root_x] < self.rank[root_y]:
                self.root[root_x] = root_y
            elif self.rank[root_x] > self.rank[root_y]:
                self.root[root_y] = root_x
            else:
                self.root[root_y] = root_x
                self.rank[root_x] += 1

    def is_connected(self, x, y):
        return self.find(x) == self.find(y)


class Solution:
    def smallestStringWithSwaps(self, s: str, pairs: List[List[int]]) -> str:
        len_s = len(s)
        uf = UnionFind(len_s)

        # iterate over the edges
        for pair in pairs:
            uf.union(pair[0], pair[1]) # pair[0] - source, pair[1] - dest

        root2v = collections.defaultdict(list)
        # root to all v in set
        for v in range(len(s)):
            root = uf.find(v)
            root2v[root].append(v)

        res = [''] * len_s

        for root in root2v:
            # vs - single set of v
            vs = root2v[root]

            chars = []

            # fill chars as s[v]
            for v in vs:
                chars.append(s[v])
            chars.sort()

            """
            [0, 3] -> [c, a] (we have set 0(root)->3)
            chars = [c,a]
            chars.sort = [a,c]
            res[0] = a
            res[3] = c
            repeat for all disjoint set
            """
            for idx, v in enumerate(vs):
                res[v] = chars[idx]

        return ''.join(res)
