         */
        private int[] helper(int[] prices, int i, int j){
            int[] result = {0,prices[i],prices[j]}; // base case 
            if(i == j) return result;
            int m = i + (j - i) / 2 ;
            int[] left = helper(prices,i,m); // left half
            int[] right = helper(prices,m+1,j); // right half
