    string pushDominoes(string s) 
    {
        vector<int>l,r;
        int n=s.size(),i=0;
        bool ch=false;
		//storing positions of l and r
        for(int i=0;i<n;i++)
        {     
            if(s[i]=='R')
            {
                if(ch)r[r.size()-1]=i;
                else r.push_back(i);
                ch=true;
            }else if(s[i]=='L')
            {
                l.push_back(i);
                ch=false;
            }
        }

        for(i=0;i<r.size();i++)
        {
            int index=binarysearch(l,r[i]);
            if(index==-1)break;
            // cout<<r[i]<<" "<<l[index]<<endl;
            int mid=l[index]-r[i]-1;
            mid/=2;
            if(mid==0)continue;
            int ss=r[i]+1,e=l[index]-1,c=mid;

