class Solution {
public:
    string pushDominoes(string s) {
        int n=size(s);
        queue<pair<int,char>>q;//queue to store the index and character at that index
        for(int i=0;i<n;i++)
        {
            if(s[i]=='L' or s[i]=='R') //pushing all the L and R into our queue
                q.push({i,s[i]});
        }
        vector<pair<int,char>>temp;//a temp vector 
        while(!q.empty())
        {
            int sz=q.size();
            while(sz--)
            {
                auto top=q.front();
                q.pop();
                int index=top.first;
                char ch=top.second;

                if(ch=='L')//if we encounter an L from queue
                {
                    if(index-2>=0 and s[index-2]=='R') continue;//to chck for condition .R.LL
                    if(index-1>=0 and s[index-1]=='.'){
                        q.push({index-1,'L'});
                        temp.push_back({index-1,'L'});
                    }
                }
                else if(ch=='R')
                {
                    if(index+2<n and s[index+2]=='L') continue;//to chck for condition .R.L...
                    if(index+1<n and s[index+1]=='.'){
                        q.push({index+1,'R'});
                        temp.push_back({index+1,'R'});
                    }
                }
            }
            for( auto i:temp)//after a level of BFS is done update the original string 
            {
                pair<int,char>&p=i;
                s[p.first]=p.second;
            }
        }
        return s;//return the new string
    }
};
