string pushDominoes(string d) {
        int n = d.size(), i = 0, prev = 0;
        string ans(n, '.');//initialize ans with '.'s
        while(i<n){
            while(i<n && d[i] == '.')i++;
            if(i<n && d[i] == 'L'){
                //if encountered an L first, then update from prev to this position all as 'L'
                while(prev <= i)
                    ans[prev++] = 'L';
                i++;
            }
            else if(i<n && d[i] == 'R'){
                //if encountered an 'R', then search for 'L' on the right side
                while(i<n && d[i] == 'R'){//to reduce redundancy, just traverse all the run of 'R's.
                    ans[i] = 'R';//also keep on updating the 'R' given in the original string
                    i++;
                }
                prev = i;
                while(i<n && d[i] == '.')i++;
                if((i<n && d[i] == 'R') || i == n){
                    //we encountered another 'R', or reached the end, so update all the '.'s to 'R' as no collision
                    while(prev < i)
                        ans[prev++] = 'R';
                }
                else if(i<n && d[i] == 'L'){
                    int p1 = i-1;
                    //we encountered an 'L', so using two pointers, update R and L, as long as the pointers don't collide
                    while(prev < p1){
                        ans[prev++] = 'R';
                        ans[p1--] = 'L';
                    }
                }
            }
            prev = i;
        }
        return  ans;
    }
