class Solution {
public:
    //adding L and R at ends do not change the configuration
    //we have to divide the total string into parts, thats why adding L and R at ends for cases like ...L..R...
    //parts can be : L...L , R...R, L...R, R...L
    //L...L->all dots inside it will become L
    //R...R-> all dots inside it  will become R
    //L...R-> all dots remains unchanged
    //R...L->if dots are even then half R half L
    //else mid remains unchanged and one half R and other half L
    void solvefor(vector<char>&arr,int i,int j)
    {
        // i and j are pointer pointing to L or R in the parititon L/R...R/L
        //now check for all the 4 conditions

        //case 1 L....L , convert all to L 
        if(arr[i]=='L' && arr[j]=='L')
        {
            for(int k=i+1;k<j;k++)
            {
                arr[k]='L';
            }
        }
        //case 2 R....R, convert all to R
        else if(arr[i]=='R' && arr[j]=='R')
        {
            for(int k=i+1;k<j;k++)
            {
                arr[k]='R';
            }
        }

        //case 3 L...R, no change in middle;
        else if(arr[i]=='L' && arr[j]=='R')
        {
            //nothing to do
        }

        //case 4 R...L,check . are even or odd and do accordingly
        else if(arr[i]=='R' && arr[j]=='L')
        {
            int diff=j-i;
            int mid=(j+i)/2;
            //case of odd dots, as positions are shifted
            if(diff%2==0)
            {
               for(int k=i+1;k<mid;k++)
               {
                   arr[k]='R';
               }
                for(int k=mid+1;k<j;k++)  
                {
                   arr[k]='L'; 
                }
            }
            //case of even number of . so R....L converts to RRRLLL
            else
            {
                for(int k=i+1;k<=mid;k++)
               {
                   arr[k]='R';
               }
                for(int k=mid+1;k<j;k++)  
                {
                   arr[k]='L'; 
                }
            }
        }

    }
    string pushDominoes(string dominoes) {
        int n=dominoes.size();
        vector<char>arr(n+2);
        arr[0]='L';
        arr[n+1]='R';
        //L 1-String-n-2 R
        for(int i=1;i<arr.size()-1;i++)
        {
            arr[i]=dominoes[i-1];
            // cout<<arr[i];
        }

        int j=0;
        int k =1;
        //j->Lk->...L...R..LR..R -> K counts number of '.' and stops on L or R
        //in above case j will be at L and K will be at L so solve for L...L
        while(k<arr.size())
        {
            while(arr[k]=='.')
            {
                k++;
            }
            //solve only for cases like L...L , not for LL
            if(k-j>1)
            {
                solvefor(arr,j,k);
            }
            j=k;
            k++;
        }
        string ans="";
        for(int i=1;i<arr.size()-1;i++)
        {
            ans+=arr[i];
        }
        return ans;
    }
}; 
