class Solution {
public:
    string pushDominoes(string dominoes) {
        queue<pair<int,int>> q;
        for(int i=0;i<dominoes.size();i++) {
            if(dominoes[i]=='L')
                q.push({0,i});
            else if(dominoes[i]=='R')
                q.push({2,i});
        }
        while(!q.empty()) {
            int size=q.size();
            unordered_map<int,char> um;
            for(int i=0;i<size;i++) {
                int side=q.front().first;
                int idx=q.front().second;
                q.pop();
                if(side==0 && idx-1>=0) {
                    if(dominoes[idx-1]=='.') {
                        if(um.find(idx-1)!=um.end())
                            um[idx-1]='.';
                        else {
                            um[idx-1]='L';
                            q.push({0,idx-1});
                        }
                    }
                } else if(side==2 && idx+1<=dominoes.size()-1) {
                    if(dominoes[idx+1]=='.') {
                        if(um.find(idx+1)!=um.end())
                            um[idx+1]='.';
                        else {
                            um[idx+1]='R';
                            q.push({2,idx+1});
                        }
                    }
                }
            }
            for(auto x:um)
                dominoes[x.first]=x.second;
        }
        return dominoes;
    }
};
