                if (left[i + 1] < 0) {
                    left[i] = left[i + 1] - 1;
                }
                continue;
            }
        }
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < left.length; i++) {
            if (left[i] + right[i] == 0) {
                sb.append('.');
            }else if (left[i] == 0 || right[i] == 0){
